<?php
 class Omx_Hooks_Block_Admin_Bulk_Form extends Mage_Adminhtml_Block_Widget_Form { protected function _prepareForm () { $form = new Varien_Data_Form(array( 'id' => 'edit_form', 'action' => $this->getUrl('*/*/*', array()), 'method' => 'post' )); $fieldset = $form->addFieldset('bulk_orders', array('legend' => Mage::helper('hooks')->__('Send bulk orders'))); $fieldset->addField( 'options', 'select', array( 'name' => 'options', 'title' => Mage::helper('hooks')->__('Options'), 'label' => Mage::helper('hooks')->__('Options'), 'required' => true, 'options' => array( 1 => Mage::helper('hooks')->__('resend previously sent'), 2 => Mage::helper('hooks')->__('skip previously sent'), ) ) ); $fieldset->addField( 'min', 'text', array( 'name' => 'min', 'title' => Mage::helper('hooks')->__('Start value'), 'label' => Mage::helper('hooks')->__('Start value'), 'required' => true ) ); $fieldset->addField( 'max', 'text', array( 'name' => 'max', 'title' => Mage::helper('hooks')->__('End value'), 'label' => Mage::helper('hooks')->__('End value'), 'required' => true ) ); $form->setUseContainer(true); $this->setForm($form); return parent::_prepareForm(); } }