<?php
 class Omx_Hooks_Block_Admin_Bulk_Results extends Mage_Adminhtml_Block_Abstract { public function _construct(){ $this->_blockGroup = 'hooks'; $this->_mode = 'search results'; $this->_controller = 'admin'; $this->_headerText = Mage::helper('hooks')->__('Search Results: ' ); } protected function _toHtml () { $string = '<h1>Results</h1>'; $results = $this->getData('sentOrders'); if(count($results)){ $string .= "<ul>"; foreach ($results as $orderId => $omxId) { $link = $this->getUrl('*/*/view', array( 'id' => $omxId )); $string .= "<li><a href='". $link ."'>". $orderId ."</a></li>"; } $string .= "</ul>"; return $string; } } }