<?php
 class Omx_Hooks_Block_Admin_Search_Form extends Mage_Adminhtml_Block_Widget_Form { protected function _prepareForm () { $form = new Varien_Data_Form(array( 'id' => 'edit_form', 'action' => $this->getUrl('*/*/*', array()), 'method' => 'post' )); $fieldset = $form->addFieldset('search_order', array('legend' => Mage::helper('hooks')->__('Search for a Magento Order ID'))); $fieldset->addField( 'search', 'text', array( 'name' => 'search', 'title' => Mage::helper('hooks')->__('Order ID (100000001)'), 'label' => Mage::helper('hooks')->__('Order ID'), 'required' => true, ) ); $form->setUseContainer(true); $this->setForm($form); return parent::_prepareForm(); } }